import React, { useEffect } from "react";
import "./ProductSinglePage.scss";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  fetchAsyncSingleProduct,
  getProductSingle,
  getProductSingleStatus,
} from "../../store/ProductSlice";
import { STATUS } from "../../utils/status";
import Loader from "../../components/Loader/Loader";
import { formatPrice } from "../../utils/helpers";
const ProductSinglePage = () => {
  const { id } = useParams();
  // console.log(id);
  const dispatch = useDispatch();
  const product = useSelector(getProductSingle);
  const productSingleStatus = useSelector(getProductSingleStatus);

  //getting single product
  useEffect(() => {
    dispatch(fetchAsyncSingleProduct(id));
  }, []);
  // console.log(product);

  let discountedPrice =
    product?.price - product?.price * (product?.discountPercentage / 100);
  if (productSingleStatus === STATUS.LOADING) {
    return <Loader />;
  }
  return (
    <div className=" py-5  bg-whitesmoke">
      <div className="container">
        <div className="product-single-content bg-white grid">
          <div className="product-img">
            <div className="product-img-zoom">
              <img
                src={product ? (product.images ? product.images[0] : "") : ""}
                alt="singleproduct"
              />
            </div>

            <div className="product-img-thumbs flex align-center justify-center ">
              <div className="thumb-img">
                <img
                  src={product ? (product.images ? product.images[1] : "") : ""}
                  alt="singleproduct"
                />
              </div>
              <div className="thumb-img">
                <img
                  src={product ? (product.images ? product.images[2] : "") : ""}
                  alt="singleproduct"
                />
              </div>
              <div className="thumb-img">
                <img
                  src={product ? (product.images ? product.images[3] : "") : ""}
                  alt="singleproduct"
                />
              </div>
            </div>
          </div>

          <div className="product-item-info">
              <div className="product-details font-manrope">
                <div className="title fs-xl">{product?.title}</div>
                <div className="">{product?.description}</div>
              </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductSinglePage;
