import React from 'react'
import { formatPrice } from '../../utils/helpers'
const CartModal = ({carts}) => {
  return (
    <>
     <h4>Recently Added Products</h4>
     {
       (carts.length>0) ?
       (
        <div>
          {
            carts.map(cart ,idx=>{
              return(
                <div className="flex">
                <img src={cart?.thumbnail} alt="img_thumbnail"/>
                <p>{cart?.title}</p>
                <p>{formatPrice(cart?.discountedPrice)}</p>
                </div>
              )

            })
          }
        </div>
       ):(
        <div>
          <p>No Products.</p>
        </div>
       )
     }
    </>
  )
}

export default CartModal
