import React from "react";
import { Link } from "react-router-dom";
import { formatPrice } from "../../utils/helpers";
import "./Product.scss";
import "../../App.scss";
const Product = ({ product }) => {
  console.log(product);
  return (
    <>
      <Link to={`/product/${product?.id}`} key={product?.id} >
      <div className="product-item">

            <div className='category '>{product?.category}</div>
            <div className="product-item-img">
                <img className="img-cover" src={product?.images[0]} alt={product.title} />
            </div>
            <div className="product-item-info">
                <div className=" brand mx-5">
                <span>Brand:</span>
                <span className="fs-sm text-bold">{product?.brand}</span>
                </div>
                <div className="title flex justify-center">{product?.title}</div>
                <div className="product-price flex align-center justify-center">
                <span className="old-price fs-base">{formatPrice(product?.price)}</span>
                <span className="new-price">
                    {formatPrice(product?.discountedPrice)}
                </span>
                <span className="discount text-danger fw-4 ">
                    ({product?.discountedPercentage} % OFF)
                </span>
                </div>
            </div>
      </div>
      </Link>
    </>
  );
};

export default Product;
