import React from 'react'
import { formatPrice } from '../../utils/helpers'
const CartModal = ({carts}) => {
  return (

    <div className="cart-model">
    <h4 >Recently Added Products</h4>

       {
       (carts.length>0) ? (
        <>
          {
            carts.map((cart,idx)=>{
              return(
                <div className="flex">
                  <img src={cart?.thumbnail} alt="imgthumbnail"/>
                  <div>{cart?.title}</div>
                </div>
              )
            })
          }
        </>
       ):(
        <></>
       )
       }    


    </div>


  )
}

export default CartModal
