import React, { useEffect } from "react";
import "./ProductSinglePage.scss";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  fetchAsyncSingleProduct,
  getProductSingle,
  getProductSingleStatus,
} from "../../store/ProductSlice";
import { STATUS } from "../../utils/status";
import Loader from "../../components/Loader/Loader";
import { formatPrice } from "../../utils/helpers";
import { useState } from "react";
import { addToCart,setCartMessageOff,setCartMessageOn,getCartMessageStatus } from "../../store/CartSlice";
import CartMessage from "../../components/CartMessage/CartMessage"
const ProductSinglePage = () => {
  const { id } = useParams();
  // console.log(id);
  const dispatch = useDispatch();
  const product = useSelector(getProductSingle);
  const productSingleStatus = useSelector(getProductSingleStatus);
  const [quantity, setQuantity] = useState(1);
  const cartMessageStatus = useSelector(getCartMessageStatus);
  //getting single product
  useEffect(() => {
    dispatch(fetchAsyncProductSingle(id));

    if (cartMessageStatus) {
      setTimeout(() => {
        dispatch(setCartMessageOff());
      }, 2000);
    }
  }, [cartMessageStatus]);

  // console.log(product);

  let discountedPrice =
    product?.price - product?.price * (product?.discountPercentage / 100);
  if (productSingleStatus === STATUS.LOADING) {
    return <Loader />;
  }
  const Decrease = () => {
    setQuantity((prevqty) => {
      let qty = prevqty - 1;
      if (qty < 1) qty = 1;
      return qty;
    });
  };
  const Increase = () => {
    setQuantity((prevqty) => {
      let qty = prevqty + 1;
      if (qty > product?.stock) qty = product?.stock;
      return qty;
    });
  };
  const addToCartHandler = (product) => {
    let discountedPrice=(product?.price)-(product?.price * (product?.discountPercentage / 100));
    let totalPrice=quantity*discountedPrice;
    dispatch(addToCart({...product,quantity:quantity,totalPrice,discountedPrice}));
    dispatch(setCartMessageOn(true));
  };
  return (
    <div className=" py-5  bg-whitesmoke">
      <div className="container">
        <div className="product-single-content grid">
          <div className="product-img">
            <div className="product-img-zoom">
              <img
                src={product ? (product.images ? product.images[1] : "") : ""}
                alt="singleproduct"
              />
            </div>

            <div className="product-img-thumbs flex align-center justify-center ">
              <div className="thumb-img">
                <img
                  src={product ? (product.images ? product.images[0] : "") : ""}
                  alt="singleproduct"
                />
              </div>
              <div className="thumb-img">
                <img
                  src={product ? (product.images ? product.images[2] : "") : ""}
                  alt="singleproduct"
                />
              </div>
              <div className="thumb-img">
                <img
                  src={product ? (product.images ? product.images[3] : "") : ""}
                  alt="singleproduct"
                />
              </div>
            </div>
          </div>

          <div className="product-item-info">
            <div className="product-details font-manrope py-4">
              <div className="title fs-xl text-start ">{product?.title}</div>
              <div className="fs-lg text-start py-3">
                {product?.description}
              </div>

              <div className="info flex align-center flex-wrap">
                <div className="rating">
                  <span className="text-orange fw-5">Rating:</span>
                  <span className="fw-5">{product?.rating}</span>
                </div>
                <div className="vert-line"></div>
                <div className="Brand">
                  <span className="text-orange fw-5">Brand:</span>
                  <span className="fw-5">{product?.brand}</span>
                </div>
                <div className="vert-line"></div>
                <div className="Category">
                  <span className="text-orange fw-5">Category:</span>
                  <span className="fw-5">{product?.category}</span>
                </div>
              </div>

              <div className="price">
                <div className=" flex ">
                  <span className="old-price fs-lg">
                    {formatPrice(product?.price)}
                  </span>
                  <span className="mx-2 fs-sm ">Inclusive of all taxes</span>
                </div>
                <div className="flex align-center">
                  <span className="fs-xl text-orange text-bold">
                    {formatPrice(discountedPrice)}
                  </span>
                  <span className=" discount mx-2 fs-xs bg-orange text-white">
                    {product?.discountPercentage} %OFF
                  </span>
                </div>
              </div>

              <div className="quantity flex align-center py-5">
                <span className="text-start fw-5">Quantity:</span>
                <div className="flex align-center mx-3">
                  <button
                    className="decrease flex align-center justify-center"
                    onClick={() => Decrease()}
                  >
                    <i className="fas fa-minus"></i>
                  </button>
                  <div className="quantity-text flex align-center justify-center">
                    {quantity}
                  </div>
                  <button
                    className="increase flex align-center justify-center"
                    onClick={() => Increase()}
                  >
                    <i className="fas fa-plus"></i>
                  </button>
                </div>
                {/* out of order stock */}
                {product?.stock === 0 ? (
                  <div className="qty-error text-uppercase bg-danger text-white fs-12 ls-1 mx-2 fw-5">
                    out of stock
                  </div>
                ) : (
                  ""
                )}
              </div>

              <div className="btns text-start">
                <button className="CartBtn">
                  <i className="fas fa-shopping-cart text-orange"></i>
                  <span
                    onClick={addToCartHandler(product)}
                    className="mx-2 text-orange fs-sm text-bold"
                  >
                    Add To Cart
                  </span>
                </button>
                <button className="buy-now mx-4">
                  <span className="btn-text text-white text-bold fs-sm">
                    Buy Now
                  </span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      {cartMessageStatus && <CartMessage />}
    </div>
  );
};

export default ProductSinglePage;
