import React, { useEffect } from "react";
import "./HomePage.scss";

import { STATUS } from "../../utils/status";
import HeaderSlider from "../../components/Slider/Slider";
import {
  fetchAsyncProducts,
  getAllProducts,
  getAllProductsStatus,
} from "../../store/ProductSlice";
import { useDispatch, useSelector } from "react-redux";
import { getAllCategories } from "../../store/CategorySlice";
import Loader from "../../components/Loader/Loader";
import ProductList from "../../components/ProductList/ProductList";
const HomePage = () => {
  const dispatch = useDispatch();
  const categories = useSelector(getAllCategories);

  useEffect(() => {
    dispatch(fetchAsyncProducts(50));
  }, []);
  //  console.log(categories);
  const products = useSelector(getAllProducts);
  // console.log(products);
  const productStatus = useSelector(getAllProductsStatus);

  //Randomizing products in productlist
  // const tempProducts = [];
  // if (products.length > 0) {
  //   for (let i in products) {
  //     let randomIndex = Math.floor(Math.random() * products.length);
  //     // console.log(randomIndex);

  //     while (tempProducts.includes(products[randomIndex])) {
  //       randomIndex = Math.floor(Math.random() * products.length);
  //     }
  //     tempProducts[i] = products[randomIndex];
  //     // console.log(tempProducts);
  //   }
  // }


  //Filtering of products 
  let CategoryProductOne = products.filter(
    (product) => product.category === categories[0]
  );
  // console.log(CategoryProductOne);
  let CategoryProductTwo = products.filter(
    (product) => product.category === categories[1]
  );
  let CategoryProductThree = products.filter(
    (product) => product.category === categories[2]
  );
  let CategoryProductFour = products.filter(
    (product) => product.category === categories[3]
  );
  return (
    <>
      <div className="slider-wrapper">
        <HeaderSlider />
      </div>
      <div className="bg-whitesmoke py-6">
        <div className="container">
          <div className="category">
            <div className="category-item ">
              <div className="title">
                <h3 className="fs-xl">See Our Products</h3>
              </div>
            </div>

            {productStatus === STATUS.LOADING ? (
              <Loader />
            ) : (
              <ProductList products={tempProducts} />
            )}
          </div>

          <div className="category">
            <div className="category-item ">
              <div className="title">
                <h3 className="fs-xl">{categories[0]}</h3>
              </div>
            </div>

            {productStatus === STATUS.LOADING ? (
              <Loader />
            ) : (
              <ProductList products={CategoryProductOne} />
            )}
          </div>

          <div className="category">
            <div className="category-item ">
              <div className="title">
                <h3 className="fs-xl">{categories[1]}</h3>
              </div>
            </div>

            {productStatus === STATUS.LOADING ? (
              <Loader />
            ) : (
              <ProductList products={CategoryProductTwo} />
            )}
          </div>

          <div className="category">
            <div className="category-item ">
              <div className="title">
                <h3 className="fs-xl">{categories[2]}</h3>
              </div>
            </div>

            {productStatus === STATUS.LOADING ? (
              <Loader />
            ) : (
              <ProductList products={CategoryProductThree} />
            )}
          </div>

          <div className="category">
            <div className="category-item ">
              <div className="title">
                <h3 className="fs-xl">{categories[3]}</h3>
              </div>
            </div>

            {productStatus === STATUS.LOADING ? (
              <Loader />
            ) : (
              <ProductList products={CategoryProductFour} />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default HomePage;
