import {Loader} from "../../components/Loader/Loader"
import {ProductList} from "../../components/ProductList/ProductList"
import { fetchAsyncSearchProduct, getSearchProducts,getSearchProductsStatus,setSearchTerm,clearSearch } from '../../store/SearchSlice';
