import React,{useEffect} from 'react'
import "./ProductSinglePage.scss"
import { useParams } from 'react-router-dom'
import { useSelector,useDispatch } from 'react-redux'
import {fetchAsyncSingleProduct,getProductSingle,getProductSingleStatus} from "../../store/ProductSlice"
import {STATUS} from "../../utils/status";
import Loader from "../../components/Loader/Loader";
import { formatPrice } from "../../utils/helpers";
const ProductSinglePage = () => {

    const {id}=useParams();
    // console.log(id);
    const dispatch=useDispatch();
    const product=useSelector(getProductSingle);
    const productSingleStatus=useSelector(getProductSingleStatus);

    //getting single product 
    useEffect(()=>{
        dispatch(fetchAsyncSingleProduct(id))
    },[])
    // console.log(product);

    let discountedPrice =(product?.price) - (product?.price * (product?.discountPercentage / 100));
    if(productSingleStatus===STATUS.LOADING){
        return <Loader/>
    }
  return (
    <div className='py-5 bg-whitesmoke'>
        <div className='product-single'>
            <div className='container'>
                ProductSinglePage
                <img src={product?(product.images ? product.images[1] : "") : ""} alt="singleproduct"/>
      </div>
      </div>
    </div>
  )
}

export default ProductSinglePage
