import React from 'react'
import { formatPrice } from '../../utils/helpers'
import "./CartModal.scss";
const CartModal = ({carts}) => {
  return (

    <div className="cart-modal">
    <h1 className='text-orange'>Recently Added Items</h1>
    {
      (carts.length>0) ? (
        <div className="cart-modal-list grid">
            {carts.map((cart,idx)=>{
              return(
                <div className="cart-modal-item grid " key={idx}>
                      <div className="cart-img flex" key={idx}>
                        <img src={cart?.thumbnail} alt="thumbnail"/>
                        <div className='text-orange fs'>{cart?.title}</div>
                      <div className='text-orange'>{cart?.discountedPrice}</div>
                      </div>

                </div>
              )

            })}
        </div>
      ):(
        <div>No Products</div>
      )
    }

    </div>


  )
}

export default CartModal
