import "./App.scss";
// react router v6
import { BrowserRouter, Routes, Route } from "react-router-dom";
// pages
import {
  Home,
  CategoryProduct,
  ProductSingle,
  Cart,
  Search,
} from "./Pages/index";
// components
import React,{useState} from "react"
import Header from "./components/Header/Header";
import Sidebar from "./components/Sidebar/Sidebar";
import Footer from "./components/Footer/Footer";
import store from "./store/store";
import { Provider } from "react-redux";
import Login from "./components/Login/Login";
import SignUp from "./components/SignUp/SignUp";
import PrivateRoutes from "./utils/PrivateRoutes";
import { getAuth, onAuthStateChanged } from "firebase/auth";
function App() {
  // const [authuser,setauthuser]=useState();
  const auth=getAuth();
  console.log(auth.currentUser)
  return (

    <div className="App">
      <Provider store={store}>
        <BrowserRouter>
        <>
        {auth.currentUser ? <> <Header/></>  : null } 

         </>
          {/* <Header/> */}
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<SignUp />} />
            <Route path="/" element={<PrivateRoutes Component={Home} />} />

            {/* single product route */}
            <Route
              path="product/:id"
              element={<PrivateRoutes Component={ProductSingle} />}
            />
            {/* category wise product listing route */}
            <Route
              path="category/:category"
              element={<PrivateRoutes Component={CategoryProduct} />}
            />
            {/* cart */}
            <Route path="/cart" element={<PrivateRoutes Component={Cart} />} />

            {/* searched products */}
            <Route
              path="/search/:searchTerm"
              element={<PrivateRoutes Component={Search} />}
            />

            {/*Error Page */}
          </Routes>
          <Footer />
        </BrowserRouter>
      </Provider>
    </div>
  );
}

export default App;
